import axios from "axios";
import { useEffect, useReducer } from "react";
import { useParams } from "react-router-dom";
import Col from "react-bootstrap/esm/Col";
import Row from "react-bootstrap/esm/Row";
import ListGroup from "react-bootstrap/esm/ListGroup";


const reducer = (state, action) => {
  switch (action.type) {
      case 'FETCH_REQUEST':
          return {...state, loading: true};
      case "FETCH_SUCCESS":
          return {...state, product: action.plyload, loading: false};
      case "FETCH_FAIL":
          return {...state, loading: false, error: action.playload };
      default: 
          return state;       
  }
};

function ProductScreen() {
    const params = useParams();
    const {slug } = params;

    const [{loading, error, product}, dispatch] = useReducer(reducer, {
      product: [],
      loading: true, 
      error: '',
  });
  useEffect(() => {
      const fetchData = async () => {
          dispatch({type: "FETCH_REQUEST"});
          try {
              const result = await axios.get(`/api/products/slug/${slug}`);
              dispatch({type: "FETCH_SUCCESS", plyload: result.data});
          } catch (error) {
              dispatch({type: "FETCH_FAIL", playload: error.message});
          } 
       
      };   
      fetchData(); 
  }, [slug]);

    return ( 
       loading ? <div>Carregando...</div>
       : error ? <div>{error}</div>
       : 
      <div>
        <Row>
          <Col md={6}>
           <img
               className="img-large"
               src={product.image}
               alt={product.name}
            ></img>              
          </Col>
          <Col md={3}>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <h1>{product.name}</h1>
              </ListGroup.Item>
              <ListGroup.Item>
                <Rating
                  rating={product.rating}
                  numReviews={product.numReviews}
                ></Rating>
              </ListGroup.Item>
              <ListGroup.Item>Preço : ${product.price}</ListGroup.Item>
              <ListGroup.Item>
                Descrição:
                <p>{product.description}</p>
              </ListGroup.Item>
            </ListGroup>  
          </Col>
          <Col md={3}></Col>
        </Row>
        </div>
    );
}
export default ProductScreen;
